/* Glowing mic pulse for launcher button */
:root {
  --brand-pulse-color-60: rgba(162,89,255,0.6);
  --brand-pulse-color-30: rgba(162,89,255,0.3);
  --brand-pulse-color-90: rgba(162,89,255,0.9);
  --brand-pulse-color-80: rgba(162,89,255,0.8);

  /* Circular logo colors for light mode */
  --logo-bg-light: rgba(91, 168, 255, 0.08);
  --logo-border-light: rgba(91, 168, 255, 0.15);
  --logo-glow-light: rgba(91, 168, 255, 0.3);
}

.dark {
  /* Circular logo colors for dark mode */
  --logo-bg-dark: rgba(91, 168, 255, 0.12);
  --logo-border-dark: rgba(91, 168, 255, 0.25);
  --logo-glow-dark: rgba(91, 168, 255, 0.5);
}

.mic-pulse {
  background: radial-gradient(circle at 30% 30%, rgba(255,255,255,0.35), rgba(255,255,255,0.15)), linear-gradient(135deg, #5ba8ff 0%, #a259ff 100%);
  color: #fff;
  animation: micGlow 2.5s ease-in-out infinite;
  position: relative;
  transition: all 0.3s ease;
}

.mic-pulse:hover {
  transform: scale(1.1) !important;
  box-shadow: 0 0 0 0 var(--brand-pulse-color-30), 0 0 25px var(--brand-pulse-color-80), inset 0 0 0 1px rgba(255,255,255,0.25) !important;
}

@keyframes micGlow {
  0%, 100% { 
    box-shadow: 0 0 0 0 var(--brand-pulse-color-30), 0 0 15px var(--brand-pulse-color-60), inset 0 0 0 1px rgba(255,255,255,0.15); 
    transform: scale(1); 
  }
  50% { 
    box-shadow: 0 0 0 8px rgba(162,89,255,0.0), 0 0 25px var(--brand-pulse-color-90), inset 0 0 0 1px rgba(255,255,255,0.25); 
    transform: scale(1.08); 
  }
}

/* Logo glow and pulse animations for "ready to talk" effect */
.logo-glow-pulse {
  animation: logoGlowPulse 3s ease-in-out infinite;
}

@keyframes logoGlowPulse {
  0%, 100% {
    filter: drop-shadow(0 0 8px rgba(91, 168, 255, 0.6)) drop-shadow(0 0 15px rgba(162, 89, 255, 0.4));
    opacity: 1;
  }
  50% {
    filter: drop-shadow(0 0 15px rgba(91, 168, 255, 0.9)) drop-shadow(0 0 30px rgba(162, 89, 255, 0.7));
    opacity: 0.95;
  }
}

/* Logo flicker animation for talking effect */
.logo-flicker {
  animation: logoFlicker 2s ease-in-out infinite;
}

@keyframes logoFlicker {
  0%, 100% {
    filter: drop-shadow(0 0 10px rgba(91, 168, 255, 0.7)) drop-shadow(0 0 20px rgba(162, 89, 255, 0.5));
    opacity: 1;
  }
  10%, 30%, 50%, 70%, 90% {
    filter: drop-shadow(0 0 5px rgba(91, 168, 255, 0.4)) drop-shadow(0 0 10px rgba(162, 89, 255, 0.3));
    opacity: 0.8;
  }
  20%, 40%, 60%, 80% {
    filter: drop-shadow(0 0 15px rgba(91, 168, 255, 1)) drop-shadow(0 0 30px rgba(162, 89, 255, 0.8));
    opacity: 1;
  }
}

/* Logo breathing effect - smooth appear/disappear */
.logo-breathe {
  animation: logoBreath 4s ease-in-out infinite;
}

@keyframes logoBreath {
  0%, 100% {
    filter: drop-shadow(0 0 12px rgba(91, 168, 255, 0.8)) drop-shadow(0 0 24px rgba(162, 89, 255, 0.6));
    opacity: 1;
    transform: scale(1);
  }
  50% {
    filter: drop-shadow(0 0 6px rgba(91, 168, 255, 0.4)) drop-shadow(0 0 12px rgba(162, 89, 255, 0.3));
    opacity: 0.7;
    transform: scale(0.95);
  }
}

/* Logo ready for talking - magical centered gradient glow (no expanding ripples) */
.logo-ready-talking {
  position: relative;
  animation: logoReadyTalking 3s ease-in-out infinite;
}

/* Centered magical gradient glow behind logo */
.logo-ready-talking::before {
  content: '';
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 140%;
  height: 140%;
  border-radius: 50%;
  background: radial-gradient(circle, 
    rgba(0, 255, 255, 0.4) 0%, 
    rgba(91, 168, 255, 0.5) 30%, 
    rgba(162, 89, 255, 0.6) 60%, 
    rgba(255, 16, 240, 0.4) 100%);
  filter: blur(20px);
  pointer-events: none;
  z-index: -1;
  animation: magicalGlow 4s ease-in-out infinite;
}

/* Additional outer glow layer for more depth */
.logo-ready-talking::after {
  content: '';
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 180%;
  height: 180%;
  border-radius: 50%;
  background: radial-gradient(circle, 
    rgba(91, 168, 255, 0.3) 0%, 
    rgba(162, 89, 255, 0.4) 50%, 
    transparent 100%);
  filter: blur(30px);
  pointer-events: none;
  z-index: -2;
  animation: magicalGlow 4s ease-in-out infinite 0.5s;
}

@keyframes logoReadyTalking {
  0%, 100% {
    filter: drop-shadow(0 0 15px rgba(91, 168, 255, 0.9)) drop-shadow(0 0 30px rgba(162, 89, 255, 0.7)) drop-shadow(0 0 45px rgba(0, 255, 255, 0.5));
    transform: scale(1);
  }
  33% {
    filter: drop-shadow(0 0 25px rgba(162, 89, 255, 1)) drop-shadow(0 0 50px rgba(255, 16, 240, 0.8)) drop-shadow(0 0 70px rgba(91, 168, 255, 0.6));
    transform: scale(1.05);
  }
  66% {
    filter: drop-shadow(0 0 20px rgba(0, 255, 255, 1)) drop-shadow(0 0 40px rgba(91, 168, 255, 0.9)) drop-shadow(0 0 60px rgba(162, 89, 255, 0.7));
    transform: scale(1.02);
  }
}

/* Magical gradient glow animation - stays centered, changes colors */
@keyframes magicalGlow {
  0% {
    opacity: 0.6;
    transform: translate(-50%, -50%) scale(1);
  }
  33% {
    opacity: 0.9;
    transform: translate(-50%, -50%) scale(1.1);
  }
  66% {
    opacity: 0.75;
    transform: translate(-50%, -50%) scale(1.05);
  }
  100% {
    opacity: 0.6;
    transform: translate(-50%, -50%) scale(1);
  }
}

/* Magical rainbow glow animation for 3D protruded logo */
@keyframes rainbow-glow {
  0% {
    background: linear-gradient(135deg, rgba(0,255,255,0.6) 0%, rgba(162,89,255,0.5) 50%, rgba(255,16,240,0.6) 100%);
  }
  25% {
    background: linear-gradient(135deg, rgba(255,16,240,0.6) 0%, rgba(0,255,255,0.5) 50%, rgba(162,89,255,0.6) 100%);
  }
  50% {
    background: linear-gradient(135deg, rgba(162,89,255,0.6) 0%, rgba(255,16,240,0.5) 50%, rgba(0,255,255,0.6) 100%);
  }
  75% {
    background: linear-gradient(135deg, rgba(0,255,255,0.5) 0%, rgba(162,89,255,0.6) 50%, rgba(255,16,240,0.5) 100%);
  }
  100% {
    background: linear-gradient(135deg, rgba(0,255,255,0.6) 0%, rgba(162,89,255,0.5) 50%, rgba(255,16,240,0.6) 100%);
  }
}

@keyframes soundRipple {
  0% {
    transform: scale(0.8);
    opacity: 0;
  }
  20% {
    opacity: 0.6;
  }
  100% {
    transform: scale(2.5);
    opacity: 0;
  }
}

/* ============================================
   CIRCULAR LOGO CONTAINER - Works in Dark & Light Mode
   ============================================ */

.logo-circular-container {
  position: relative;
  border-radius: 50%;
  padding: 1.5rem;
  background: light-dark(var(--logo-bg-light), var(--logo-bg-dark));
  border: 2px solid light-dark(var(--logo-border-light), var(--logo-border-dark));
  box-shadow: 0 8px 32px light-dark(var(--logo-glow-light), var(--logo-glow-dark));
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;
}

/* Dark mode override using class selector for better compatibility */
.dark .logo-circular-container {
  background: var(--logo-bg-dark);
  border-color: var(--logo-border-dark);
  box-shadow: 0 8px 32px var(--logo-glow-dark);
}

/* ============================================
   IPHONE SIRI-LIKE GLOWING MIC EFFECT
   Horizontal beam glow when recording/listening
   ============================================ */

.mic-recording-glow {
  position: relative;
  overflow: visible;
  isolation: isolate;
}

/* Wrapper for glow effects behind button */
.mic-recording-glow::before {
  content: '';
  position: absolute;
  inset: -50%;
  z-index: -1;
  pointer-events: none;
}

/* Main horizontal glow beam (like iPhone Siri) */
.mic-recording-glow::after {
  content: '';
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 300%;
  height: 60%;
  background: linear-gradient(90deg, 
    transparent 0%,
    rgba(91, 168, 255, 0.3) 15%,
    rgba(91, 168, 255, 0.6) 35%,
    rgba(162, 89, 255, 0.8) 50%,
    rgba(91, 168, 255, 0.6) 65%,
    rgba(91, 168, 255, 0.3) 85%,
    transparent 100%
  );
  filter: blur(25px);
  animation: siriBeamPulse 2s ease-in-out infinite;
  pointer-events: none;
  z-index: -1;
  border-radius: 50%;
}

@keyframes siriBeamPulse {
  0%, 100% {
    opacity: 0.7;
    transform: translate(-50%, -50%) scaleX(1);
  }
  50% {
    opacity: 1;
    transform: translate(-50%, -50%) scaleX(1.3);
  }
}

/* Enhanced logo with Siri-like glow effect */
.logo-with-siri-glow {
  position: relative;
  animation: logoSiriPulse 2s ease-in-out infinite;
}

.logo-with-siri-glow::before {
  content: '';
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 250%;
  height: 50%;
  background: linear-gradient(90deg, 
    transparent 0%,
    rgba(91, 168, 255, 0.2) 20%,
    rgba(162, 89, 255, 0.5) 50%,
    rgba(91, 168, 255, 0.2) 80%,
    transparent 100%
  );
  filter: blur(25px);
  animation: logoSiriBeam 2.5s ease-in-out infinite;
  pointer-events: none;
  z-index: -1;
  border-radius: 50%;
}

@keyframes logoSiriPulse {
  0%, 100% {
    filter: drop-shadow(0 0 15px rgba(91, 168, 255, 0.6)) drop-shadow(0 0 30px rgba(162, 89, 255, 0.4));
    transform: scale(1);
  }
  50% {
    filter: drop-shadow(0 0 25px rgba(91, 168, 255, 0.9)) drop-shadow(0 0 50px rgba(162, 89, 255, 0.7));
    transform: scale(1.05);
  }
}

@keyframes logoSiriBeam {
  0%, 100% {
    opacity: 0.5;
    transform: translate(-50%, -50%) scaleX(0.9);
  }
  50% {
    opacity: 1;
    transform: translate(-50%, -50%) scaleX(1.3);
  }
}

