@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply bg-background text-foreground;
    font-family: var(--font-inter), system-ui, sans-serif;
    font-feature-settings: "cv02", "cv03", "cv04", "cv11";
    font-variation-settings: "opsz" 32;
  }

  /* Professional scrollbar styles */
  ::-webkit-scrollbar {
    width: 6px;
    height: 6px;
  }
  ::-webkit-scrollbar-track {
    @apply bg-muted/30;
    border-radius: 8px;
  }
  ::-webkit-scrollbar-thumb {
    @apply bg-primary/40;
    border-radius: 8px;
    transition: background-color 0.2s ease;
  }
  ::-webkit-scrollbar-thumb:hover {
    @apply bg-primary/60;
  }

  /* Professional focus styles */
  :focus-visible {
    @apply outline-none ring-2 ring-primary/20 ring-offset-2 ring-offset-background;
  }

  /* Smooth transitions for theme switching */
  * {
    transition: background-color 0.2s ease, border-color 0.2s ease, color 0.2s ease;
  }
}

/* Ensure h-screen takes full viewport height on mobile */
html, body, #__next {
  height: 100%;
}
/* Fix for flex column taking full height */
#__next > div {
    min-height: 100%;
}

@layer utilities {
  /* Hide scrollbar (while keeping scroll) for horizontal tab list */
  .no-scrollbar {
    -ms-overflow-style: none; /* IE and Edge */
    scrollbar-width: none; /* Firefox */
  }
  .no-scrollbar::-webkit-scrollbar {
    display: none; /* Chrome, Safari */
  }

  /* Custom thin scrollbar for better mobile UX */
  .scrollbar-thin {
    scrollbar-width: thin;
  }
  .scrollbar-thin::-webkit-scrollbar {
    width: 4px;
    height: 4px;
  }
  .scrollbar-thumb-muted::-webkit-scrollbar-thumb {
    @apply bg-muted-foreground/30;
    border-radius: 4px;
  }
  .scrollbar-thumb-muted::-webkit-scrollbar-thumb:hover {
    @apply bg-muted-foreground/50;
  }
  .scrollbar-track-transparent::-webkit-scrollbar-track {
    background: transparent;
  }

  /* Touch-friendly tap targets */
  .touch-target {
    min-height: 44px;
    min-width: 44px;
  }

  /* Mobile-specific utilities */
  @media (max-width: 640px) {
    /* Ensure minimum text size on mobile */
    .text-xs { font-size: 0.8125rem; } /* 13px */
    .text-sm { font-size: 0.875rem; } /* 14px */
    .text-base { font-size: 1rem; } /* 16px */

    /* Better line height for readability on mobile */
    body {
      line-height: 1.6;
    }

    /* Full width inputs on mobile */
    input:not([type="checkbox"]):not([type="radio"]),
    select,
    textarea {
      width: 100%;
    }

    /* Minimum padding for cards */
    .card-mobile {
      @apply p-4;
    }
  }

  /* Horizontal scroll with fade indicators */
  .horizontal-scroll-fade {
    position: relative;
    overflow-x: auto;
    -webkit-overflow-scrolling: touch;
    scrollbar-width: thin;
  }

  .horizontal-scroll-fade::before,
  .horizontal-scroll-fade::after {
    content: '';
    position: absolute;
    top: 0;
    bottom: 0;
    width: 20px;
    pointer-events: none;
    z-index: 10;
  }

  .horizontal-scroll-fade::before {
    left: 0;
    background: linear-gradient(to right, hsl(var(--muted)), transparent);
  }

  .horizontal-scroll-fade::after {
    right: 0;
    background: linear-gradient(to left, hsl(var(--muted)), transparent);
  }

  /* Smooth scrolling for better UX */
  @media (prefers-reduced-motion: no-preference) {
    html {
      scroll-behavior: smooth;
    }
  }

  /* Mobile-friendly table wrapper */
  .mobile-table-wrapper {
    @apply overflow-x-auto -mx-4 sm:mx-0 scrollbar-thin scrollbar-thumb-muted scrollbar-track-transparent;
  }

  .mobile-table-wrapper table {
    @apply min-w-full;
  }

  /* Mobile card padding adjustments */
  .mobile-card-padding {
    @apply p-3 sm:p-4 md:p-6;
  }

  /* Mobile form spacing */
  .mobile-form-spacing {
    @apply space-y-3 sm:space-y-4;
  }

  /* Mobile button group */
  .mobile-button-group {
    @apply flex flex-col sm:flex-row gap-2 sm:gap-3;
  }

  /* Mobile grid adjustments */
  .mobile-grid {
    @apply grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-3 sm:gap-4;
  }

  /* Mobile input group */
  .mobile-input-group {
    @apply flex flex-col gap-1.5;
  }

  /* Mobile header spacing */
  .mobile-header-spacing {
    @apply space-y-2 sm:space-y-3;
  }

  /* Professional animations */
  .widget-open {
    animation: widget-open 0.4s cubic-bezier(0.16, 1, 0.3, 1) forwards;
  }
  .widget-close {
    animation: widget-close 0.3s cubic-bezier(0.5, 0, 0.75, 0) forwards;
  }
  @keyframes widget-open {
    from {
      opacity: 0;
      transform: translateY(20px) scale(0.95);
    }
    to {
      opacity: 1;
      transform: translateY(0) scale(1);
    }
  }
  @keyframes widget-close {
    from {
      opacity: 1;
      transform: translateY(0) scale(1);
    }
    to {
      opacity: 0;
      transform: translateY(20px) scale(0.95);
    }
  }

  /* Professional gradient backgrounds */
  .gradient-primary,
  .bg-gradient-primary {
    background: var(--gradient-primary);
  }
  .gradient-secondary,
  .bg-gradient-secondary {
    background: var(--gradient-secondary);
  }
  .gradient-accent,
  .bg-gradient-accent {
    background: var(--gradient-accent);
  }

  /* Professional glass effect */
  .glass {
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.2);
  }
  .dark .glass {
    background: rgba(0, 0, 0, 0.2);
    border: 1px solid rgba(255, 255, 255, 0.1);
  }

  /* Professional shadows */
  .shadow-professional {
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
  }
  .shadow-professional-lg {
    box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);
  }
  .dark .shadow-professional {
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.3), 0 2px 4px -1px rgba(0, 0, 0, 0.2);
  }
  .dark .shadow-professional-lg {
    box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.3), 0 4px 6px -2px rgba(0, 0, 0, 0.2);
  }

  /* Professional hover effects */
  .hover-lift {
    transition: transform 0.2s ease, box-shadow 0.2s ease;
  }
  .hover-lift:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 25px -8px rgba(0, 0, 0, 0.15);
  }

  .language-select-trigger > svg {
    color: hsl(var(--primary));
  }

  /* Dynamic brand color support */
  .brand-themed {
    --primary: var(--brand-primary-hsl, 221 83% 53%);
    --accent: var(--brand-primary-hsl, 221 83% 53%);
    --ring: var(--brand-primary-hsl, 221 83% 53%);
  }

  .brand-themed.dark {
    --primary: var(--brand-primary-hsl-dark, 217 91% 60%);
    --accent: var(--brand-primary-hsl-dark, 217 91% 60%);
    --ring: var(--brand-primary-hsl-dark, 217 91% 60%);
  }

  /* Professional typing indicator */
  .typing-indicator {
    display: inline-flex;
    align-items: center;
    gap: 4px;
    line-height: 1;
  }
  .typing-dot {
    width: 5px;
    height: 5px;
    border-radius: 50%;
    background-color: currentColor;
    opacity: 0.6;
    animation: typing-bounce 1.4s infinite ease-in-out;
  }
  .typing-dot:nth-child(2) { animation-delay: 0.2s; }
  .typing-dot:nth-child(3) { animation-delay: 0.4s; }
  @keyframes typing-bounce {
    0%, 80%, 100% {
      transform: translateY(0) scale(1);
      opacity: 0.6;
    }
    40% {
      transform: translateY(-4px) scale(1.1);
      opacity: 1;
    }
  }

  /* Professional card styles */
  .card-professional {
    @apply bg-card border border-border/50 rounded-xl shadow-professional;
  }
  .card-professional-hover {
    @apply card-professional hover-lift cursor-pointer;
  }

  /* Gen Z Futuristic Animations */

  /* Neon Pulse Animation */
  @keyframes neon-pulse {
    0%, 100% { 
      box-shadow: 0 0 20px rgba(0, 255, 255, 0.4),
                  0 0 40px rgba(0, 212, 255, 0.3),
                  0 0 60px rgba(162, 89, 255, 0.2),
                  inset 0 0 20px rgba(0, 255, 255, 0.1);
    }
    50% { 
      box-shadow: 0 0 30px rgba(0, 255, 255, 0.6),
                  0 0 60px rgba(0, 212, 255, 0.5),
                  0 0 90px rgba(162, 89, 255, 0.4),
                  inset 0 0 30px rgba(0, 255, 255, 0.2);
    }
  }

  .neon-pulse {
    animation: neon-pulse 2s ease-in-out infinite;
  }

  /* Cyber Gradient Animation */
  @keyframes cyber-gradient {
    0% { background-position: 0% 50%; }
    50% { background-position: 100% 50%; }
    100% { background-position: 0% 50%; }
  }

  .cyber-gradient {
    background: linear-gradient(270deg, #00FFFF, #00D4FF, #5BA8FF, #A259FF, #FF10F0);
    background-size: 400% 400%;
    animation: cyber-gradient 8s ease infinite;
  }

  /* Neon Shimmer Animation */
  @keyframes neon-shimmer {
    0% { transform: translateX(-100%); }
    100% { transform: translateX(100%); }
  }

  .neon-shimmer {
    animation: neon-shimmer 2s ease-in-out infinite;
  }

  /* Glitch Effect */
  @keyframes glitch {
    0% { transform: translate(0); }
    20% { transform: translate(-2px, 2px); }
    40% { transform: translate(-2px, -2px); }
    60% { transform: translate(2px, 2px); }
    80% { transform: translate(2px, -2px); }
    100% { transform: translate(0); }
  }

  .glitch-hover:hover {
    animation: glitch 0.3s cubic-bezier(0.25, 0.46, 0.45, 0.94) both;
  }

  /* Floating Animation (Gen Z style) */
  @keyframes float {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-10px); }
  }

  .float-element {
    animation: float 3s ease-in-out infinite;
  }

  /* Glassmorphism Effects */
  .glass-card {
    background: rgba(10, 10, 15, 0.6);
    backdrop-filter: blur(20px) saturate(180%);
    border: 1px solid rgba(0, 255, 255, 0.2);
    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
    box-shadow: 0 8px 32px rgba(0, 255, 255, 0.1),
                inset 0 1px 0 rgba(255, 255, 255, 0.1);
  }

  .glass-card:hover {
    transform: translateY(-4px);
    box-shadow: 0 20px 60px rgba(0, 255, 255, 0.3),
                0 0 80px rgba(255, 16, 240, 0.2),
                inset 0 1px 0 rgba(255, 255, 255, 0.2);
    border-color: rgba(0, 255, 255, 0.4);
  }

  /* 3D Card Depth Effect */
  .card-3d {
    transform-style: preserve-3d;
    transition: transform 0.4s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .card-3d:hover {
    transform: translateY(-8px) rotateX(2deg);
  }

  /* Futuristic Button */
  .btn-futuristic {
    @apply relative overflow-hidden;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    box-shadow: 0 0 20px rgba(0, 255, 255, 0.3);
  }

  .btn-futuristic::before {
    content: '';
    @apply absolute inset-0 bg-gradient-to-r from-transparent via-cyan-400/30 to-transparent;
    transform: translateX(-100%);
    transition: transform 0.6s;
  }

  .btn-futuristic:hover {
    box-shadow: 0 0 30px rgba(0, 255, 255, 0.6), 
                0 0 60px rgba(255, 16, 240, 0.4);
    transform: scale(1.05);
  }

  .btn-futuristic:hover::before {
    transform: translateX(100%);
  }

  /* Cyber Border Glow */
  .cyber-border {
    position: relative;
  }

  .cyber-border::before {
    content: '';
    position: absolute;
    inset: -2px;
    background: linear-gradient(45deg, #00FFFF, #00D4FF, #A259FF, #FF10F0);
    border-radius: inherit;
    opacity: 0;
    transition: opacity 0.3s ease;
    z-index: -1;
    filter: blur(8px);
  }

  .cyber-border:hover::before {
    opacity: 0.6;
  }

  /* Neon Text Glow */
  .neon-text {
    text-shadow: 0 0 10px rgba(0, 255, 255, 0.5),
                 0 0 20px rgba(0, 255, 255, 0.3),
                 0 0 30px rgba(0, 255, 255, 0.2);
  }

  .neon-text-purple {
    text-shadow: 0 0 10px rgba(178, 75, 243, 0.5),
                 0 0 20px rgba(178, 75, 243, 0.3),
                 0 0 30px rgba(178, 75, 243, 0.2);
  }

  /* Holographic Gradient */
  .holographic {
    background: linear-gradient(
      135deg,
      #00FFFF 0%,
      #00D4FF 15%,
      #5BA8FF 30%,
      #A259FF 50%,
      #FF10F0 70%,
      #FF69B4 85%,
      #00FFFF 100%
    );
    background-size: 200% 200%;
    animation: cyber-gradient 6s ease infinite;
  }

  /* ============================================
     MICRO-INTERACTIONS - DASHBOARD CARDS
     ============================================ */

  /* Interactive Card with Lift + Glow Effect */
  .card-interactive {
    position: relative;
    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
    cursor: pointer;
  }

  .card-interactive::before {
    content: '';
    position: absolute;
    inset: -2px;
    background: linear-gradient(135deg, 
      rgba(0, 255, 255, 0.3) 0%, 
      rgba(162, 89, 255, 0.3) 50%, 
      rgba(255, 16, 240, 0.3) 100%
    );
    border-radius: inherit;
    opacity: 0;
    transition: opacity 0.4s ease;
    z-index: -1;
    filter: blur(12px);
  }

  .card-interactive:hover {
    transform: translateY(-8px) scale(1.02);
    box-shadow: 0 20px 60px rgba(0, 255, 255, 0.25),
                0 10px 40px rgba(162, 89, 255, 0.15),
                0 0 80px rgba(255, 16, 240, 0.1);
  }

  .card-interactive:hover::before {
    opacity: 1;
  }

  .dark .card-interactive:hover {
    box-shadow: 0 20px 60px rgba(0, 255, 255, 0.35),
                0 10px 40px rgba(162, 89, 255, 0.25),
                0 0 100px rgba(255, 16, 240, 0.2);
  }

  /* Interactive Card - Subtle Variant (for smaller cards) */
  .card-interactive-subtle {
    position: relative;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    cursor: pointer;
  }

  .card-interactive-subtle:hover {
    transform: translateY(-4px);
    box-shadow: 0 12px 32px rgba(0, 255, 255, 0.15),
                0 6px 20px rgba(162, 89, 255, 0.1);
    border-color: rgba(0, 255, 255, 0.4);
  }

  /* Magnetic Button Effect - Elements attracted to cursor */
  .magnetic-btn {
    position: relative;
    transition: transform 0.2s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .magnetic-btn:hover {
    transform: scale(1.05);
  }

  /* ============================================
     MOBILE BOTTOM TAB BAR - FUTURISTIC DESIGN
     ============================================ */

  /* Bottom Tab Bar Container */
  .mobile-bottom-nav {
    position: fixed;
    bottom: 0;
    left: 0;
    right: 0;
    z-index: 50;
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    background: hsl(var(--background) / 0.95);
    border-top: 1px solid hsl(var(--primary) / 0.3);
    padding-bottom: env(safe-area-inset-bottom);
    box-shadow: 0 -4px 24px rgba(0, 255, 255, 0.1);
  }

  /* Tab Item Base */
  .mobile-tab-item {
    position: relative;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    gap: 0.25rem;
    min-height: 48px;
    padding: 0.5rem 0.75rem;
    color: hsl(var(--muted-foreground));
    transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
    cursor: pointer;
    -webkit-tap-highlight-color: transparent;
  }

  /* Active Tab - Cyber Gradient Text */
  .mobile-tab-item[data-active="true"] {
    color: transparent;
    background: linear-gradient(135deg, #00FFFF 0%, #5BA8FF 50%, #A259FF 100%);
    -webkit-background-clip: text;
    background-clip: text;
  }

  /* Active Tab Neon Glow Indicator */
  .mobile-tab-item[data-active="true"]::before {
    content: '';
    position: absolute;
    top: 0;
    left: 50%;
    transform: translateX(-50%);
    width: 40px;
    height: 3px;
    background: linear-gradient(90deg, #00FFFF, #A259FF);
    border-radius: 0 0 4px 4px;
    box-shadow: 0 0 12px rgba(0, 255, 255, 0.8),
                0 0 24px rgba(162, 89, 255, 0.4);
    animation: neon-pulse-tab 2s ease-in-out infinite;
  }

  /* Tab Icon Glow on Active */
  .mobile-tab-item[data-active="true"] svg {
    filter: drop-shadow(0 0 8px rgba(0, 255, 255, 0.6))
            drop-shadow(0 0 16px rgba(162, 89, 255, 0.4));
  }

  /* Tap Feedback Animation */
  .mobile-tab-item:active {
    transform: scale(0.95);
  }

  .mobile-tab-item:hover:not([data-active="true"]) {
    color: hsl(var(--foreground) / 0.8);
  }

  @keyframes neon-pulse-tab {
    0%, 100% {
      opacity: 1;
      box-shadow: 0 0 12px rgba(0, 255, 255, 0.8),
                  0 0 24px rgba(162, 89, 255, 0.4);
    }
    50% {
      opacity: 0.7;
      box-shadow: 0 0 20px rgba(0, 255, 255, 1),
                  0 0 40px rgba(162, 89, 255, 0.6);
    }
  }

  /* Glass Effect Enhancement for Desktop Nav */
  .desktop-nav-glass {
    backdrop-filter: blur(16px);
    -webkit-backdrop-filter: blur(16px);
    background: hsl(var(--background) / 0.8);
    border: 1px solid hsl(var(--primary) / 0.2);
  }

  /* Neon Accent Border for Desktop */
  .desktop-nav-accent {
    position: relative;
  }

  .desktop-nav-accent::after {
    content: '';
    position: absolute;
    inset: 0;
    border-radius: inherit;
    padding: 1px;
    background: linear-gradient(135deg, 
      hsl(var(--primary) / 0.3), 
      hsl(var(--accent-cyan) / 0.2),
      hsl(var(--primary) / 0.3)
    );
    -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    -webkit-mask-composite: xor;
    mask-composite: exclude;
    pointer-events: none;
  }

  /* Safe Area Padding for Mobile Devices */
  .safe-area-bottom {
    padding-bottom: max(0.5rem, env(safe-area-inset-bottom));
  }

  /* Responsive Tab Text */
  .mobile-tab-label {
    font-size: 0.625rem;
    font-weight: 500;
    letter-spacing: 0.025em;
  }

  @media (min-width: 640px) {
    .mobile-tab-label {
      font-size: 0.75rem;
    }
  }

  /* ============================================
     GEN Z FUTURISTIC NEON LOADING ANIMATIONS
     ============================================ */

  /* Neon shimmer sweep effect for skeletons */
  @keyframes neon-shimmer {
    0% {
      background-position: -200% 0;
    }
    100% {
      background-position: 200% 0;
    }
  }

  .animate-neon-shimmer {
    animation: neon-shimmer 2s ease-in-out infinite;
  }

  /* Neon spin for loader rings */
  @keyframes neon-spin {
    from {
      transform: rotate(0deg);
    }
    to {
      transform: rotate(360deg);
    }
  }

  .animate-neon-spin {
    animation: neon-spin 1.5s linear infinite;
    filter: drop-shadow(0 0 8px currentColor);
  }

  /* Reverse spin for counter-rotating rings */
  @keyframes neon-spin-reverse {
    from {
      transform: rotate(360deg);
    }
    to {
      transform: rotate(0deg);
    }
  }

  .animate-neon-spin-reverse {
    animation: neon-spin-reverse 2s linear infinite;
    filter: drop-shadow(0 0 6px currentColor);
  }

  /* Neon pulse for glowing cores */
  @keyframes neon-pulse {
    0%, 100% {
      opacity: 1;
      transform: scale(1);
      filter: drop-shadow(0 0 10px currentColor) brightness(1);
    }
    50% {
      opacity: 0.7;
      transform: scale(0.95);
      filter: drop-shadow(0 0 20px currentColor) brightness(1.2);
    }
  }

  .animate-neon-pulse {
    animation: neon-pulse 2s ease-in-out infinite;
  }

  /* Delayed pulse variant */
  .animate-neon-pulse-delayed {
    animation: neon-pulse 2s ease-in-out infinite;
    animation-delay: 0.5s;
  }

  /* Text pulse for loading messages */
  @keyframes neon-text-pulse {
    0%, 100% {
      opacity: 1;
    }
    50% {
      opacity: 0.6;
    }
  }

  .animate-neon-text-pulse {
    animation: neon-text-pulse 1.5s ease-in-out infinite;
  }

  /* Orbital animations for particles */
  @keyframes neon-orbit-1 {
    from {
      transform: rotate(0deg) translateY(-200%) rotate(0deg);
    }
    to {
      transform: rotate(360deg) translateY(-200%) rotate(-360deg);
    }
  }

  @keyframes neon-orbit-2 {
    from {
      transform: rotate(120deg) translateY(-200%) rotate(-120deg);
    }
    to {
      transform: rotate(480deg) translateY(-200%) rotate(-480deg);
    }
  }

  @keyframes neon-orbit-3 {
    from {
      transform: rotate(240deg) translateY(-200%) rotate(-240deg);
    }
    to {
      transform: rotate(600deg) translateY(-200%) rotate(-600deg);
    }
  }

  .animate-neon-orbit-1 {
    animation: neon-orbit-1 2s linear infinite;
  }

  .animate-neon-orbit-2 {
    animation: neon-orbit-2 2s linear infinite;
  }

  .animate-neon-orbit-3 {
    animation: neon-orbit-3 2s linear infinite;
  }

  /* Sliding progress bar animation */
  @keyframes neon-slide {
    0% {
      transform: translateX(-100%);
    }
    100% {
      transform: translateX(200%);
    }
  }

  .animate-neon-slide {
    animation: neon-slide 1.5s ease-in-out infinite;
  }

  /* Bouncing dots animation */
  @keyframes neon-dot-bounce {
    0%, 80%, 100% {
      transform: translateY(0);
      opacity: 1;
    }
    40% {
      transform: translateY(-10px);
      opacity: 0.7;
    }
  }

  .animate-neon-dot-bounce {
    animation: neon-dot-bounce 1.4s ease-in-out infinite;
  }

  /* Glitch flicker effect */
  @keyframes neon-glitch {
    0%, 100% {
      transform: translate(0);
      filter: hue-rotate(0deg);
    }
    20% {
      transform: translate(-2px, 2px);
      filter: hue-rotate(90deg);
    }
    40% {
      transform: translate(2px, -2px);
      filter: hue-rotate(180deg);
    }
    60% {
      transform: translate(-2px, -2px);
      filter: hue-rotate(270deg);
    }
    80% {
      transform: translate(2px, 2px);
      filter: hue-rotate(360deg);
    }
  }

  .animate-neon-glitch {
    animation: neon-glitch 0.3s ease-in-out;
  }

  /* Holographic blur effect */
  @keyframes neon-holographic {
    0%, 100% {
      filter: blur(0px) hue-rotate(0deg);
      opacity: 1;
    }
    25% {
      filter: blur(2px) hue-rotate(90deg);
      opacity: 0.9;
    }
    50% {
      filter: blur(4px) hue-rotate(180deg);
      opacity: 0.8;
    }
    75% {
      filter: blur(2px) hue-rotate(270deg);
      opacity: 0.9;
    }
  }

  .animate-neon-holographic {
    animation: neon-holographic 3s ease-in-out infinite;
  }

  /* Neon glow shadows for enhanced effects */
  .shadow-neon-core {
    box-shadow: 
      0 0 10px currentColor,
      0 0 20px currentColor,
      0 0 30px currentColor,
      inset 0 0 10px rgba(255, 255, 255, 0.3);
  }

  .shadow-neon-strong {
    box-shadow: 
      0 0 15px currentColor,
      0 0 30px currentColor,
      0 0 45px currentColor;
  }

  .shadow-neon-particle {
    box-shadow: 
      0 0 5px currentColor,
      0 0 10px currentColor,
      0 0 15px currentColor;
  }

  .shadow-neon-bar {
    box-shadow: 
      0 0 10px currentColor,
      0 0 20px currentColor;
  }

  /* ============================================
     CONFETTI ANIMATION
     ============================================ */

  @keyframes confetti-fall {
    0% {
      transform: translateY(0) translateX(0) rotate(0deg);
      opacity: 1;
    }
    100% {
      transform: translateY(var(--confetti-y)) translateX(var(--confetti-x)) rotate(var(--confetti-rotation));
      opacity: 0;
    }
  }

  .animate-confetti-fall {
    animation: confetti-fall 3s linear forwards;
  }

  /* Respect prefers-reduced-motion */
  @media (prefers-reduced-motion: reduce) {
    .animate-neon-shimmer,
    .animate-neon-spin,
    .animate-neon-spin-reverse,
    .animate-neon-pulse,
    .animate-neon-pulse-delayed,
    .animate-neon-text-pulse,
    .animate-neon-orbit-1,
    .animate-neon-orbit-2,
    .animate-neon-orbit-3,
    .animate-neon-slide,
    .animate-neon-dot-bounce,
    .animate-neon-glitch,
    .animate-neon-holographic,
    .animate-confetti-fall {
      animation: none;
      opacity: 1;
    }

    .shadow-neon-core,
    .shadow-neon-strong,
    .shadow-neon-particle,
    .shadow-neon-bar {
      box-shadow: none;
    }
  }
}

