import { NextResponse } from 'next/server';
import { getCollections } from '@/lib/mongodb';
import { rateLimit } from '@/lib/security';

// Public endpoint to return a sanitized tenant config for embedded widget use.
// Only exposes fields required for rendering and chatting, without sensitive data.
const corsHeaders: Record<string, string> = {
  'Access-Control-Allow-Origin': '*',
  'Access-Control-Allow-Methods': 'GET, OPTIONS',
  'Access-Control-Allow-Headers': 'Content-Type'
};

export async function OPTIONS() {
  return new NextResponse(null, { status: 204, headers: corsHeaders });
}

export async function GET(request: Request) {
  try {
    // Public endpoint for widget: do not enforce origin checks (CORS headers are set below)

    const limited = rateLimit(request, 'public_tenant_config_get', 120, 60_000);
    if (limited) return limited;

    const { searchParams } = new URL(request.url);
    const id = searchParams.get('id');
    const agentId = searchParams.get('agentId') || undefined;
    if (!id) {
      return NextResponse.json({ message: 'Missing id' }, { status: 400, headers: corsHeaders });
    }

    const { tenants } = await getCollections();
    const t: any = await tenants.findOne({ id });
    if (!t) {
      return NextResponse.json({ message: 'Not found' }, { status: 404, headers: corsHeaders });
    }

    // Include full training contexts with extracted text for AI to use
    const sanitizeCtx = (ctx: any) => ({
      // Legacy format support
      websiteUrl: (ctx?.websiteUrl || '').trim(),
      docInfo: (ctx?.docInfo || '').trim(),
      uploadedDocContent: ctx?.uploadedDocContent || '',
      // New format support (agent-specific training)
      id: ctx?.id,
      sourceInfo: ctx?.sourceInfo,
      extractedText: ctx?.extractedText || '',
      wordCount: ctx?.wordCount,
      characterCount: ctx?.characterCount,
      createdAt: ctx?.createdAt,
    });

    const sanitized = {
      id: t.id,
      name: t.name,
      companyLogoUrl: t.companyLogoUrl || '',
      brandColor: t.brandColor || '#2795f2',
      companyDetails: t.companyDetails || '',
      trainingWebsiteUrl: t.trainingWebsiteUrl || '',
      trainingDocInfo: t.trainingDocInfo || '',
      logoHint: t.logoHint || '',
      companyWebsiteUrl: t.companyWebsiteUrl || '',
      assignedPlanId: t.assignedPlanId || 'free',
      status: t.status || 'Active',
      supportedLanguages: Array.isArray(t.supportedLanguages) ? t.supportedLanguages : [{ code: 'en-US', name: 'English' }],
      // Respect explicit empty string (means: show circle icon with no text)
      launcherButtonText: t.launcherButtonText !== undefined ? t.launcherButtonText : 'Help?',
      launcherButtonIcon: t.launcherButtonIcon || 'mic',
      launcherButtonSize: t.launcherButtonSize || 'medium',
      launcherButtonStyle: t.launcherButtonStyle || 'normal',
      launcherButtonAnimation: t.launcherButtonAnimation || 'pulse',
      launcherButtonPosition: t.launcherButtonPosition || 'bottom-right',
      launcherAutoOpenDelay: t.launcherAutoOpenDelay || 'none',
      // Tenant-level contexts, sanitized
      trainingContexts: Array.isArray(t.trainingContexts) ? t.trainingContexts.map(sanitizeCtx) : [],
      // Agents with only safe fields
      agents: Array.isArray(t.agents) ? t.agents.map((a: any) => ({
        id: a.id,
        name: a.name,
        description: a.description,
        avatarUrl: a.avatarUrl,
        avatarHint: a.avatarHint,
        greeting: a.greeting,
        websiteUrl: a.websiteUrl,
        voice: a.voice,
        tone: a.tone,
        responseStyle: a.responseStyle,
        expertiseLevel: a.expertiseLevel,
        customInstructions: a.customInstructions,
        trainingContexts: Array.isArray(a.trainingContexts) ? a.trainingContexts.map(sanitizeCtx) : [],
      })) : [],
    };

    // Optionally include the selected agent if agentId provided
    const selectedAgent = agentId && Array.isArray(t.agents)
      ? t.agents.find((a: any) => a.id === agentId)
      : undefined;

    // Effective launcher configuration (future-proof for agent overrides)
    const effective = {
      text: sanitized.launcherButtonText,
      icon: sanitized.launcherButtonIcon,
      size: sanitized.launcherButtonSize,
      style: sanitized.launcherButtonStyle,
      animation: sanitized.launcherButtonAnimation,
      position: sanitized.launcherButtonPosition,
      autoOpenDelay: sanitized.launcherAutoOpenDelay,
      brandColor: sanitized.brandColor,
    };

    return NextResponse.json({ tenant: sanitized, agent: selectedAgent ? { id: selectedAgent.id, name: selectedAgent.name } : undefined, effective }, { headers: corsHeaders });
  } catch (e) {
    console.error('[API /api/public/tenant-config GET] Error', e);
    return NextResponse.json({ message: 'Server error' }, { status: 500, headers: corsHeaders });
  }
}

src/lib/token-counter.ts.content:
/**
 * Token counting utilities for tracking AI usage
 * 
 * This provides token estimation for text content.
 * For more accurate counting, integrate with the AI model's actual token counter.
 */

/**
 * Estimates token count from text using a simple heuristic
 * Average token is ~4 characters for English text
 * This is a rough approximation - actual count may vary by 10-20%
 */
export function estimateTokenCount(text: string): number {
  if (!text) return 0;

  // Remove extra whitespace
  const cleanText = text.trim().replace(/\s+/g, ' ');

  // Average ~4 characters per token (common heuristic)
  const charCount = cleanText.length;
  const estimatedTokens = Math.ceil(charCount / 4);

  return estimatedTokens;
}

/**
 * Estimates token count for structured content (text + images)
 */
export function estimateContentTokens(content: string | any[]): number {
  if (typeof content === 'string') {
    return estimateTokenCount(content);
  }

  let totalTokens = 0;

  if (Array.isArray(content)) {
    for (const part of content) {
      if (part.text) {
        totalTokens += estimateTokenCount(part.text);
      }
      if (part.media) {
        // Images typically count as ~258 tokens for vision models
        totalTokens += 258;
      }
    }
  }

  return totalTokens;
}

/**
 * Calculates total tokens for a conversation
 */
export function calculateConversationTokens(messages: Array<{ content: string | any[] }>): number {
  return messages.reduce((total, msg) => {
    return total + estimateContentTokens(msg.content);
  }, 0);
}

src/app/api/auth/delete-user/route.ts.content:
import { NextResponse } from 'next/server';
import { z } from 'zod';
import { ObjectId } from 'mongodb';
import { getCollections } from '@/lib/mongodb';
import { getSessionFromCookies } from '@/lib/auth';
import { assertAllowedOrigin, rateLimit } from '@/lib/security';

/**
 * Endpoint for deleting users by super admin
 * Only accessible by existing super admins
 * Note: Super admins cannot delete themselves
 */
const DeleteUserSchema = z.object({
  userId: z.string().min(1),
});

export async function DELETE(request: Request) {
  try {
    const blocked = assertAllowedOrigin(request);
    if (blocked) return blocked;
    const limited = rateLimit(request, 'delete_user', 10, 60_000);
    if (limited) return limited;

    // Check if user is authenticated and is a super admin
    const session = await getSessionFromCookies();
    if (!session || session.role !== 'superadmin') {
      return NextResponse.json({ 
        message: 'Unauthorized. Only super admins can delete users.' 
      }, { status: 403 });
    }

    const data = await request.json();
    const { userId } = DeleteUserSchema.parse(data);

    const { users, tenants } = await getCollections();

    // Find the user to be deleted
    const userToDelete = await users.findOne({ _id: new ObjectId(userId) });
    if (!userToDelete) {
      return NextResponse.json({ 
        message: 'User not found.' 
      }, { status: 404 });
    }

    // Prevent super admin from deleting themselves
    if (userToDelete._id.toString() === session.userId) {
      return NextResponse.json({ 
        message: 'You cannot delete your own account.' 
      }, { status: 400 });
    }

    // If the user is an admin with a tenant, delete or reassign the tenant
    let tenantAction = 'none';
    if (userToDelete.role === 'admin' && userToDelete.tenantId) {
      const tenant = await tenants.findOne({ id: userToDelete.tenantId });
      if (tenant) {
        // For now, we'll delete the tenant. In the future, you might want to reassign it
        await tenants.deleteOne({ id: userToDelete.tenantId });
        tenantAction = 'deleted';
      }
    }

    // Delete the user
    const deleteResult = await users.deleteOne({ _id: userToDelete._id });

    if (deleteResult.deletedCount === 0) {
      return NextResponse.json({ 
        message: 'Failed to delete user.' 
      }, { status: 500 });
    }

    console.log('[API /api/auth/delete-user] User deleted:', {
      deletedUserId: userId,
      deletedUserEmail: userToDelete.email,
      deletedUserRole: userToDelete.role,
      tenantAction,
      deletedBy: session.email
    });

    return NextResponse.json({
      message: 'User deleted successfully.',
      deletedUser: {
        id: userId,
        email: userToDelete.email,
        role: userToDelete.role,
        tenantId: userToDelete.tenantId
      },
      tenantAction
    }, { status: 200 });

  } catch (error: any) {
    console.error('[API /api/auth/delete-user] Error:', error);

    if (error instanceof z.ZodError) {
      return NextResponse.json({ 
        message: 'Invalid input. User ID is required.' 
      }, { status: 400 });
    }

    if (error instanceof SyntaxError) {
      return NextResponse.json({ 
        message: 'Invalid request format.' 
      }, { status: 400 });
    }

    return NextResponse.json({
      message: 'An unexpected error occurred.',
    }, { status: 500 });
  }
}

/**
 * GET endpoint to retrieve all users (for super admin management)
 */
export async function GET(request: Request) {
  try {
    const blocked = assertAllowedOrigin(request);
    if (blocked) return blocked;
    const limited = rateLimit(request, 'get_users', 20, 60_000);
    if (limited) return limited;

    // Check if user is authenticated and is a super admin
    const session = await getSessionFromCookies();
    if (!session || session.role !== 'superadmin') {
      return NextResponse.json({ 
        message: 'Unauthorized. Only super admins can view users.' 
      }, { status: 403 });
    }

    const { users, tenants } = await getCollections();

    // Get all users and their associated tenant information
    const allUsers = await users.find({}).toArray();

    const usersWithTenantInfo = await Promise.all(
      allUsers.map(async (user) => {
        let tenantInfo = null;
        if (user.tenantId) {
          const tenant = await tenants.findOne({ id: user.tenantId });
          if (tenant) {
            tenantInfo = {
              id: tenant.id,
              name: tenant.name,
              status: tenant.status
            };
          }
        }

        return {
          id: user._id.toString(),
          email: user.email,
          role: user.role,
          tenantId: user.tenantId,
          tenant: tenantInfo,
          createdAt: user.createdAt
        };
      })
    );

    return NextResponse.json({
      users: usersWithTenantInfo
    }, { status: 200 });

  } catch (error: any) {
    console.error('[API /api/auth/delete-user GET] Error:', error);
    return NextResponse.json({
      message: 'An unexpected error occurred.',
    }, { status: 500 });
  }
}

