'use client';

import Image from 'next/image';
import { useTheme } from 'next-themes';
import { cn } from '@/lib/utils';
import { useState, useEffect } from 'react';

interface ThemeLogoProps {
  size?: number;
  width?: number;
  height?: number;
  className?: string;
  animate?: boolean;
  glowIntensity?: 'low' | 'medium' | 'high';
}

export function ThemeLogo({ 
  size = 80, 
  width, 
  height,
  className = '',
  animate = true,
  glowIntensity = 'medium'
}: ThemeLogoProps) {
  const { resolvedTheme } = useTheme();
  const [mounted, setMounted] = useState(false);

  const finalWidth = width || size;
  const finalHeight = height || size;

  useEffect(() => {
    setMounted(true);
  }, []);

  const isDark = mounted ? resolvedTheme === 'dark' : true;

  // Optimized glow effects - reduced blur for better performance
  const glowStylesDark = {
    low: 'drop-shadow(0 0 12px rgba(0,255,255,0.5)) drop-shadow(0 2px 6px rgba(0,0,0,0.3))',
    medium: 'drop-shadow(0 0 18px rgba(0,255,255,0.6)) drop-shadow(0 4px 8px rgba(0,0,0,0.3))',
    high: 'drop-shadow(0 0 24px rgba(0,255,255,0.7)) drop-shadow(0 6px 12px rgba(0,0,0,0.4))'
  };

  const glowStylesLight = {
    low: 'drop-shadow(0 0 10px rgba(178,75,243,0.4)) drop-shadow(0 2px 6px rgba(0,0,0,0.2))',
    medium: 'drop-shadow(0 0 15px rgba(178,75,243,0.5)) drop-shadow(0 4px 8px rgba(0,0,0,0.25))',
    high: 'drop-shadow(0 0 20px rgba(178,75,243,0.6)) drop-shadow(0 6px 10px rgba(0,0,0,0.3))'
  };

  const filterValue = isDark ? glowStylesDark[glowIntensity] : glowStylesLight[glowIntensity];

  return (
    <div className={cn("relative inline-flex items-center justify-center group", className)} style={{
      width: finalWidth,
      height: finalHeight
    }}>
      {/* Gradient background circle for light mode - makes white logo visible */}
      {!isDark && (
        <div className="absolute rounded-full bg-gradient-to-br from-cyan-400 via-purple-500 to-pink-500 opacity-100" 
             style={{ 
               width: finalWidth, 
               height: finalHeight,
               left: '50%',
               top: '50%',
               transform: 'translate(-50%, -50%)'
             }} 
        />
      )}

      {/* Optimized glow effect - single layer for better performance */}
      <div className={cn(
        "absolute rounded-full opacity-30 blur-xl",
        isDark 
          ? "bg-gradient-to-r from-cyan-400/40 via-purple-500/40 to-pink-400/40" 
          : "bg-gradient-to-r from-cyan-300/30 via-purple-400/30 to-pink-300/30"
      )} style={{ 
        width: finalWidth * 1.3, 
        height: finalHeight * 1.3,
        left: '50%',
        top: '50%',
        transform: 'translate(-50%, -50%)',
        animation: animate ? 'pulse 3s ease-in-out infinite' : 'none'
      }} />

      {/* Logo image with 3D transform - centered */}
      <div className="relative z-10" style={{
        transform: animate ? 'perspective(1000px) rotateX(5deg)' : 'none',
        transition: 'transform 0.3s ease',
        width: finalWidth,
        height: finalHeight
      }}>
        <Image 
          src="/logo.png" 
          alt="Voice Chat AI" 
          width={finalWidth} 
          height={finalHeight} 
          className={cn(
            "object-contain transition-all duration-500 relative z-10",
            animate && "group-hover:scale-110 group-hover:rotate-3"
          )}
          style={{
            filter: filterValue,
            transform: 'translateZ(20px)',
            width: finalWidth,
            height: finalHeight
          }}
        />
      </div>

      {/* Optimized hover glow - reduced blur for better performance */}
      {animate && (
        <div className={cn(
          "absolute rounded-full opacity-0 group-hover:opacity-40 transition-opacity duration-300 pointer-events-none blur-xl",
          "bg-gradient-to-r from-cyan-400 via-purple-500 to-pink-500"
        )} style={{
          width: finalWidth * 1.4,
          height: finalHeight * 1.4,
          left: '50%',
          top: '50%',
          transform: 'translate(-50%, -50%)'
        }} />
      )}
    </div>
  );
}

export function AnimatedLogo({ 
  size = 80,
  width,
  height, 
  className = '',
  withRipple = true,
  glowIntensity = 'high'
}: ThemeLogoProps & { withRipple?: boolean }) {
  const { resolvedTheme } = useTheme();
  const [mounted, setMounted] = useState(false);

  const finalWidth = width || size;
  const finalHeight = height || size;

  useEffect(() => {
    setMounted(true);
  }, []);

  const isDark = mounted ? resolvedTheme === 'dark' : true;

  return (
    <div className={cn("relative inline-flex items-center justify-center group", className)} style={{ 
      perspective: '1000px',
      width: finalWidth,
      height: finalHeight
    }}>
      {/* Optimized glow effect - single layer for better performance */}
      {withRipple && (
        <div 
          className={cn(
            "absolute rounded-full blur-xl pointer-events-none opacity-50",
            isDark 
              ? "bg-gradient-to-r from-cyan-400/40 via-purple-500/50 to-pink-400/40" 
              : "bg-gradient-to-r from-cyan-300/30 via-purple-400/40 to-pink-300/30"
          )} 
          style={{ 
            width: finalWidth * 1.5,
            height: finalHeight * 1.5,
            left: '50%',
            top: '50%',
            transform: 'translate(-50%, -50%)',
            animation: 'magicalGlow 3s ease-in-out infinite'
          }} 
        />
      )}

      <ThemeLogo 
        width={finalWidth} 
        height={finalHeight}
        animate={true}
        glowIntensity={glowIntensity}
        className="relative z-10"
      />
    </div>
  );
}

src/hooks/use-swipe.ts.content:
'use client';

import { useRef, useState, useCallback, useEffect } from 'react';

export interface SwipeHandlers {
  onSwipeLeft?: () => void;
  onSwipeRight?: () => void;
  onSwipeUp?: () => void;
  onSwipeDown?: () => void;
}

export interface SwipeConfig {
  threshold?: number;
  velocityThreshold?: number;
  preventScroll?: boolean;
}

export interface SwipeState {
  isSwiping: boolean;
  swipeDirection: 'left' | 'right' | 'up' | 'down' | null;
  swipeDistance: number;
  swipePercentage: number;
}

export function useSwipe(handlers: SwipeHandlers, config: SwipeConfig = {}) {
  const {
    threshold = 100,
    velocityThreshold = 0.3,
    preventScroll = false
  } = config;

  const touchStartX = useRef<number>(0);
  const touchStartY = useRef<number>(0);
  const touchEndX = useRef<number>(0);
  const touchEndY = useRef<number>(0);
  const touchStartTime = useRef<number>(0);
  const containerWidth = useRef<number>(0);

  const [swipeState, setSwipeState] = useState<SwipeState>({
    isSwiping: false,
    swipeDirection: null,
    swipeDistance: 0,
    swipePercentage: 0
  });

  const resetSwipeState = useCallback(() => {
    setSwipeState({
      isSwiping: false,
      swipeDirection: null,
      swipeDistance: 0,
      swipePercentage: 0
    });
  }, []);

  const handleTouchStart = useCallback((e: TouchEvent | React.TouchEvent) => {
    touchStartX.current = e.touches[0].clientX;
    touchStartY.current = e.touches[0].clientY;
    touchStartTime.current = Date.now();

    const target = e.currentTarget as HTMLElement;
    containerWidth.current = target.offsetWidth;

    setSwipeState(prev => ({
      ...prev,
      isSwiping: true
    }));

    if (preventScroll) {
      e.preventDefault();
    }
  }, [preventScroll]);

  const handleTouchMove = useCallback((e: TouchEvent | React.TouchEvent) => {
    if (!swipeState.isSwiping) return;

    touchEndX.current = e.touches[0].clientX;
    touchEndY.current = e.touches[0].clientY;

    const diffX = touchEndX.current - touchStartX.current;
    const diffY = touchEndY.current - touchStartY.current;

    const absDiffX = Math.abs(diffX);
    const absDiffY = Math.abs(diffY);

    let direction: 'left' | 'right' | 'up' | 'down' | null = null;

    if (absDiffX > absDiffY && absDiffX > 10) {
      direction = diffX > 0 ? 'right' : 'left';
      if (preventScroll) {
        e.preventDefault();
      }
    } else if (absDiffY > absDiffX && absDiffY > 10) {
      direction = diffY > 0 ? 'down' : 'up';
    }

    const distance = direction === 'left' || direction === 'right' ? absDiffX : absDiffY;
    const percentage = containerWidth.current > 0 
      ? Math.min((distance / containerWidth.current) * 100, 100)
      : 0;

    setSwipeState({
      isSwiping: true,
      swipeDirection: direction,
      swipeDistance: distance,
      swipePercentage: percentage
    });
  }, [swipeState.isSwiping, preventScroll]);

  const handleTouchEnd = useCallback(() => {
    if (!swipeState.isSwiping) return;

    const diffX = touchEndX.current - touchStartX.current;
    const diffY = touchEndY.current - touchStartY.current;
    const timeDiff = (Date.now() - touchStartTime.current) / 1000;

    const absDiffX = Math.abs(diffX);
    const absDiffY = Math.abs(diffY);

    const velocityX = absDiffX / timeDiff;
    const velocityY = absDiffY / timeDiff;

    if (absDiffX > absDiffY) {
      if (absDiffX > threshold || velocityX > velocityThreshold) {
        if (diffX > 0 && handlers.onSwipeRight) {
          handlers.onSwipeRight();
        } else if (diffX < 0 && handlers.onSwipeLeft) {
          handlers.onSwipeLeft();
        }
      }
    } else {
      if (absDiffY > threshold || velocityY > velocityThreshold) {
        if (diffY > 0 && handlers.onSwipeDown) {
          handlers.onSwipeDown();
        } else if (diffY < 0 && handlers.onSwipeUp) {
          handlers.onSwipeUp();
        }
      }
    }

    resetSwipeState();
  }, [swipeState.isSwiping, threshold, velocityThreshold, handlers, resetSwipeState]);

  const swipeHandlers = {
    onTouchStart: handleTouchStart,
    onTouchMove: handleTouchMove,
    onTouchEnd: handleTouchEnd
  };

  return {
    swipeHandlers,
    swipeState,
    resetSwipeState
  };
}

export function useMouseSwipe(handlers: SwipeHandlers, config: SwipeConfig = {}) {
  const {
    threshold = 100,
    velocityThreshold = 0.3
  } = config;

  const mouseStartX = useRef<number>(0);
  const mouseStartY = useRef<number>(0);
  const mouseEndX = useRef<number>(0);
  const mouseEndY = useRef<number>(0);
  const mouseStartTime = useRef<number>(0);
  const isDragging = useRef<boolean>(false);
  const containerWidth = useRef<number>(0);

  const [swipeState, setSwipeState] = useState<SwipeState>({
    isSwiping: false,
    swipeDirection: null,
    swipeDistance: 0,
    swipePercentage: 0
  });

  const resetSwipeState = useCallback(() => {
    setSwipeState({
      isSwiping: false,
      swipeDirection: null,
      swipeDistance: 0,
      swipePercentage: 0
    });
  }, []);

  const handleMouseDown = useCallback((e: MouseEvent | React.MouseEvent) => {
    isDragging.current = true;
    mouseStartX.current = e.clientX;
    mouseStartY.current = e.clientY;
    mouseStartTime.current = Date.now();

    const target = e.currentTarget as HTMLElement;
    containerWidth.current = target.offsetWidth;

    setSwipeState(prev => ({
      ...prev,
      isSwiping: true
    }));
  }, []);

  const handleMouseMove = useCallback((e: MouseEvent | React.MouseEvent) => {
    if (!isDragging.current) return;

    mouseEndX.current = e.clientX;
    mouseEndY.current = e.clientY;

    const diffX = mouseEndX.current - mouseStartX.current;
    const diffY = mouseEndY.current - mouseStartY.current;

    const absDiffX = Math.abs(diffX);
    const absDiffY = Math.abs(diffY);

    let direction: 'left' | 'right' | 'up' | 'down' | null = null;

    if (absDiffX > absDiffY && absDiffX > 10) {
      direction = diffX > 0 ? 'right' : 'left';
    } else if (absDiffY > absDiffX && absDiffY > 10) {
      direction = diffY > 0 ? 'down' : 'up';
    }

    const distance = direction === 'left' || direction === 'right' ? absDiffX : absDiffY;
    const percentage = containerWidth.current > 0 
      ? Math.min((distance / containerWidth.current) * 100, 100)
      : 0;

    setSwipeState({
      isSwiping: true,
      swipeDirection: direction,
      swipeDistance: distance,
      swipePercentage: percentage
    });
  }, []);

  const handleMouseUp = useCallback(() => {
    if (!isDragging.current) return;

    isDragging.current = false;

    const diffX = mouseEndX.current - mouseStartX.current;
    const diffY = mouseEndY.current - mouseStartY.current;
    const timeDiff = (Date.now() - mouseStartTime.current) / 1000;

    const absDiffX = Math.abs(diffX);
    const absDiffY = Math.abs(diffY);

    const velocityX = absDiffX / timeDiff;
    const velocityY = absDiffY / timeDiff;

    if (absDiffX > absDiffY) {
      if (absDiffX > threshold || velocityX > velocityThreshold) {
        if (diffX > 0 && handlers.onSwipeRight) {
          handlers.onSwipeRight();
        } else if (diffX < 0 && handlers.onSwipeLeft) {
          handlers.onSwipeLeft();
        }
      }
    } else {
      if (absDiffY > threshold || velocityY > velocityThreshold) {
        if (diffY > 0 && handlers.onSwipeDown) {
          handlers.onSwipeDown();
        } else if (diffY < 0 && handlers.onSwipeUp) {
          handlers.onSwipeUp();
        }
      }
    }

    resetSwipeState();
  }, [threshold, velocityThreshold, handlers, resetSwipeState]);

  const swipeHandlers = {
    onMouseDown: handleMouseDown,
    onMouseMove: handleMouseMove,
    onMouseUp: handleMouseUp
  };

  return {
    swipeHandlers,
    swipeState,
    resetSwipeState
  };
}

src/app/dashboard/_components/swipeable-.content:
'use client';

import React, { useState } from 'react';
import { useSwipe } from '@/hooks/use-swipe';
import { Card, CardContent } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { cn } from '@/lib/utils';
import { 
  Phone, 
  Mail, 
  Calendar, 
  MessageSquare, 
  Archive, 
  Sparkles,
  ChevronRight,
  User,
  Clock
} from 'lucide-react';
import { format } from 'date-fns';

interface Lead {
  _id?: string;
  id?: string;
  customerInfo: string;
  customerName?: string;
  customerEmail?: string;
  customerPhone?: string;
  date?: string;
  status?: string;
  summary?: string;
  summaryData?: {
    problemsDiscussed?: string[];
    solutionsProvided?: string[];
    suggestionsGiven?: string[];
    customerName?: string | null;
    customerEmail?: string | null;
    customerPhone?: string | null;
  };
  history?: any[];
  imageUrl?: string | null;
  isReturningCustomer?: boolean;
  totalCustomerSessions?: number;
  isAnonymous?: boolean;
}

interface SwipeableLeadCardProps {
  lead: Lead;
  onContact?: () => void;
  onArchive?: () => void;
  onViewDetails?: () => void;
  className?: string;
}

export function SwipeableLeadCard({
  lead,
  onContact,
  onArchive,
  onViewDetails,
  className
}: SwipeableLeadCardProps) {
  const [isActioned, setIsActioned] = useState(false);
  const isMobile = typeof window !== 'undefined' && window.innerWidth < 768;

  const { swipeHandlers, swipeState } = useSwipe({
    onSwipeLeft: () => {
      if (onArchive && isMobile) {
        setIsActioned(true);
        setTimeout(() => onArchive(), 200);
      }
    },
    onSwipeRight: () => {
      if (onContact && isMobile) {
        setIsActioned(true);
        setTimeout(() => onContact(), 200);
      }
    }
  }, {
    threshold: 100,
    preventScroll: false
  });

  const getSwipeProgress = () => {
    if (!swipeState.isSwiping) return 0;
    return Math.min(swipeState.swipePercentage, 100);
  };

  const swipeProgress = getSwipeProgress();
  const showLeftAction = swipeState.swipeDirection === 'left' && swipeProgress > 20;
  const showRightAction = swipeState.swipeDirection === 'right' && swipeProgress > 20;

  const customerName = lead.customerName || lead.summaryData?.customerName || lead.customerInfo?.split(',')[0] || 'Anonymous';
  const customerEmail = lead.customerEmail || lead.summaryData?.customerEmail;
  const customerPhone = lead.customerPhone || lead.summaryData?.customerPhone;
  const leadDate = lead.date ? new Date(lead.date) : new Date();

  const aiSummary = lead.summaryData?.problemsDiscussed?.[0] || 
                    lead.summary?.substring(0, 100) || 
                    'AI analysis not available yet';

  return (
    <div className={cn('relative group', className)}>
      {/* Swipe action indicators - mobile only */}
      {isMobile && (
        <>
          {/* Archive (Left swipe) */}
          <div 
            className={cn(
              'absolute right-0 top-0 bottom-0 w-24 flex items-center justify-center transition-opacity duration-200 rounded-r-xl',
              'bg-gradient-to-l from-red-500/20 to-transparent border-r-2 border-red-500/30',
              showLeftAction ? 'opacity-100' : 'opacity-0'
            )}
          >
            <Archive className="w-6 h-6 text-red-400" />
          </div>

          {/* Contact (Right swipe) */}
          <div 
            className={cn(
              'absolute left-0 top-0 bottom-0 w-24 flex items-center justify-center transition-opacity duration-200 rounded-l-xl',
              'bg-gradient-to-r from-green-500/20 to-transparent border-l-2 border-green-500/30',
              showRightAction ? 'opacity-100' : 'opacity-0'
            )}
          >
            <Phone className="w-6 h-6 text-green-400" />
          </div>
        </>
      )}

      <Card 
        {...(isMobile ? swipeHandlers : {})}
        className={cn(
          'transition-all duration-200 border border-primary/10 bg-card/50 backdrop-blur-sm overflow-hidden',
          'hover:border-primary/20 hover:shadow-lg hover:shadow-primary/5',
          isMobile && swipeState.isSwiping && 'cursor-grabbing',
          isMobile && !swipeState.isSwiping && 'cursor-grab',
          isActioned && 'opacity-0 scale-95',
          showLeftAction && 'translate-x-[-8px]',
          showRightAction && 'translate-x-[8px]'
        )}
        style={{
          transform: isMobile && swipeState.isSwiping 
            ? `translateX(${swipeState.swipeDirection === 'left' ? -swipeProgress/3 : swipeProgress/3}px)` 
            : undefined
        }}
      >
        <CardContent className="p-4">
          {/* Header */}
          <div className="flex items-start gap-3 mb-3">
            <Avatar className="w-10 h-10 border-2 border-primary/20">
              <AvatarImage src={lead.imageUrl || undefined} />
              <AvatarFallback className="bg-gradient-to-br from-cyan-400/20 to-purple-500/20">
                {customerName.charAt(0).toUpperCase()}
              </AvatarFallback>
            </Avatar>

            <div className="flex-1 min-w-0">
              <div className="flex items-center gap-2 mb-1">
                <h3 className="font-semibold text-sm truncate">{customerName}</h3>
                {lead.isReturningCustomer && (
                  <Badge variant="outline" className="text-xs border-cyan-400/30 text-cyan-400">
                    Returning
                  </Badge>
                )}
              </div>

              <div className="flex items-center gap-3 text-xs text-muted-foreground">
                <span className="flex items-center gap-1">
                  <Clock className="w-3 h-3" />
                  {format(leadDate, 'MMM d, h:mm a')}
                </span>
                {lead.totalCustomerSessions && lead.totalCustomerSessions > 1 && (
                  <span className="flex items-center gap-1">
                    <MessageSquare className="w-3 h-3" />
                    {lead.totalCustomerSessions} chats
                  </span>
                )}
              </div>
            </div>

            <Badge 
              variant={lead.status === 'contacted' ? 'default' : 'secondary'}
              className={cn(
                'text-xs',
                lead.status === 'contacted' 
                  ? 'bg-green-500/20 text-green-400 border-green-500/30' 
                  : 'bg-cyan-500/20 text-cyan-400 border-cyan-500/30'
              )}
            >
              {lead.status || 'New'}
            </Badge>
          </div>

          {/* AI Summary Section */}
          <div className="mb-3 p-3 rounded-lg bg-gradient-to-br from-primary/5 via-accent-cyan/5 to-accent-purple/5 border border-primary/10">
            <div className="flex items-center gap-2 mb-2">
              <Sparkles className="w-3 h-3 text-cyan-400 animate-neon-pulse" />
              <span className="text-xs font-medium text-cyan-400">AI Summary</span>
            </div>
            <p className="text-sm text-muted-foreground line-clamp-2">{aiSummary}</p>
          </div>

          {/* Contact Info */}
          <div className="space-y-1.5 mb-3">
            {customerEmail && (
              <div className="flex items-center gap-2 text-xs text-muted-foreground">
                <Mail className="w-3 h-3 text-purple-400" />
                <span className="truncate">{customerEmail}</span>
              </div>
            )}
            {customerPhone && (
              <div className="flex items-center gap-2 text-xs text-muted-foreground">
                <Phone className="w-3 h-3 text-cyan-400" />
                <span>{customerPhone}</span>
              </div>
            )}
          </div>

          {/* Actions */}
          <div className="flex gap-2">
            {/* Desktop buttons - always visible */}
            {!isMobile && (
              <>
                <Button
                  size="sm"
                  variant="outline"
                  onClick={onContact}
                  className="flex-1 border-green-500/30 text-green-400 hover:bg-green-500/10 hover:text-green-300"
                >
                  <Phone className="w-3 h-3 mr-1" />
                  Contact
                </Button>
                <Button
                  size="sm"
                  variant="outline"
                  onClick={onArchive}
                  className="border-red-500/30 text-red-400 hover:bg-red-500/10 hover:text-red-300"
                >
                  <Archive className="w-3 h-3" />
                </Button>
              </>
            )}

            {/* Mobile swipe hint or view details */}
            {isMobile ? (
              <Button
                size="sm"
                variant="ghost"
                onClick={onViewDetails}
                className="flex-1 text-primary hover:text-primary/80"
              >
                {!swipeState.isSwiping && (
                  <>
                    <span className="text-xs">Swipe for actions</span>
                    <ChevronRight className="w-3 h-3 ml-1" />
                  </>
                )}
              </Button>
            ) : (
              <Button
                size="sm"
                variant="ghost"
                onClick={onViewDetails}
                className="text-primary hover:text-primary/80"
              >
                <ChevronRight className="w-4 h-4" />
              </Button>
            )}
          </div>

          {/* Mobile swipe progress indicator */}
          {isMobile && swipeState.isSwiping && swipeProgress > 0 && (
            <div className="mt-3 h-1 bg-muted/20 rounded-full overflow-hidden">
              <div 
                className={cn(
                  'h-full transition-all duration-100',
                  showLeftAction && 'bg-gradient-to-r from-red-400 to-red-500',
                  showRightAction && 'bg-gradient-to-r from-green-400 to-green-500'
                )}
                style={{ width: `${swipeProgress}%` }}
              />
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

export default SwipeableLeadCard;

src/components/ui/loading/neon-loader.ts.content:
'use client';

import React from 'react';
import { cn } from '@/lib/utils';

interface NeonLoaderProps {
  size?: 'sm' | 'md' | 'lg';
  variant?: 'cyber' | 'pulse' | 'orbit';
  text?: string;
  className?: string;
}

export function NeonLoader({ 
  size = 'md', 
  variant = 'cyber',
  text,
  className 
}: NeonLoaderProps) {
  const sizeClasses = {
    sm: 'w-8 h-8',
    md: 'w-12 h-12',
    lg: 'w-16 h-16'
  };

  const textSizeClasses = {
    sm: 'text-xs',
    md: 'text-sm',
    lg: 'text-base'
  };

  if (variant === 'cyber') {
    return (
      <div className={cn('flex flex-col items-center justify-center gap-3', className)}>
        <div className={cn('relative', sizeClasses[size])}>
          {/* Outer rotating ring with neon glow */}
          <div className="absolute inset-0 rounded-full border-2 border-transparent border-t-cyan-400 border-r-purple-500 animate-neon-spin" />

          {/* Middle ring with reverse rotation */}
          <div className="absolute inset-[15%] rounded-full border-2 border-transparent border-b-pink-500 border-l-cyan-400 animate-neon-spin-reverse" />

          {/* Inner glowing core */}
          <div className="absolute inset-[30%] rounded-full bg-gradient-to-br from-cyan-400 via-purple-500 to-pink-500 animate-neon-pulse shadow-neon-core" />
        </div>

        {text && (
          <p className={cn(
            'font-medium bg-gradient-to-r from-cyan-400 via-purple-500 to-pink-500 bg-clip-text text-transparent animate-neon-text-pulse',
            textSizeClasses[size]
          )}>
            {text}
          </p>
        )}
      </div>
    );
  }

  if (variant === 'pulse') {
    return (
      <div className={cn('flex flex-col items-center justify-center gap-3', className)}>
        <div className={cn('relative', sizeClasses[size])}>
          {/* Pulsing rings */}
          <div className="absolute inset-0 rounded-full bg-gradient-to-br from-cyan-400 to-purple-500 animate-neon-pulse opacity-50" />
          <div className="absolute inset-[10%] rounded-full bg-gradient-to-br from-purple-500 to-pink-500 animate-neon-pulse-delayed opacity-70" />
          <div className="absolute inset-[20%] rounded-full bg-gradient-to-br from-cyan-400 to-pink-500 animate-neon-pulse shadow-neon-strong" />
        </div>

        {text && (
          <p className={cn(
            'font-medium bg-gradient-to-r from-cyan-400 via-purple-500 to-pink-500 bg-clip-text text-transparent',
            textSizeClasses[size]
          )}>
            {text}
          </p>
        )}
      </div>
    );
  }

  // orbit variant
  return (
    <div className={cn('flex flex-col items-center justify-center gap-3', className)}>
      <div className={cn('relative', sizeClasses[size])}>
        {/* Central core */}
        <div className="absolute inset-[35%] rounded-full bg-gradient-to-br from-cyan-400 to-purple-500 shadow-neon-core" />

        {/* Orbiting particles */}
        <div className="absolute inset-0">
          <div className="absolute top-0 left-1/2 w-2 h-2 -ml-1 rounded-full bg-cyan-400 shadow-neon-particle animate-neon-orbit-1" />
          <div className="absolute top-0 left-1/2 w-2 h-2 -ml-1 rounded-full bg-purple-500 shadow-neon-particle animate-neon-orbit-2" />
          <div className="absolute top-0 left-1/2 w-2 h-2 -ml-1 rounded-full bg-pink-500 shadow-neon-particle animate-neon-orbit-3" />
        </div>
      </div>

      {text && (
        <p className={cn(
          'font-medium bg-gradient-to-r from-cyan-400 via-purple-500 to-pink-500 bg-clip-text text-transparent',
          textSizeClasses[size]
        )}>
          {text}
        </p>
      )}
    </div>
  );
}

// Minimal loading bar variant
export function NeonLoadingBar({ className }: { className?: string }) {
  return (
    <div className={cn('w-full h-1 bg-muted/20 rounded-full overflow-hidden', className)}>
      <div className="h-full bg-gradient-to-r from-cyan-400 via-purple-500 to-pink-500 animate-neon-slide shadow-neon-bar" />
    </div>
  );
}

// Dots loader variant
export function NeonDots({ className }: { className?: string }) {
  return (
    <div className={cn('flex items-center gap-2', className)}>
      <div className="w-2 h-2 rounded-full bg-cyan-400 shadow-neon-particle animate-neon-dot-bounce" style={{ animationDelay: '0ms' }} />
      <div className="w-2 h-2 rounded-full bg-purple-500 shadow-neon-particle animate-neon-dot-bounce" style={{ animationDelay: '150ms' }} />
      <div className="w-2 h-2 rounded-full bg-pink-500 shadow-neon-particle animate-neon-dot-bounce" style={{ animationDelay: '300ms' }} />
    </div>
  );
}

