export function NeonSkeleton({ 
  className,
  variant = 'default',
  lines = 1,
  animate = true
}: NeonSkeletonProps) {
  const baseClasses = cn(
    'bg-gradient-to-r from-muted/40 via-muted/60 to-muted/40 rounded-md',
    animate && 'animate-neon-shimmer bg-[length:200%_100%]',
    className
  );

  if (variant === 'text') {
    return (
      <div className="space-y-2">
